<script>
import burgerMenu from "./BurgerMenu";
import { mapGetters } from "vuex";

export default {
  data() {
    return {
      windowWidth: window.innerWidth
    };
  },
  created() {
    window.addEventListener("resize", this.handleWindowResize);
  },
  components: {
    burgerMenu
  },
  computed: {
    ...mapGetters(["menuItems", "isLoggedIn"])
  },
  props: ["closeMenu", "burgerMenuOpen", "toggleMenu"],
  methods: {
    slideOpen() {
      if (this.burgerMenuOpen === false) {
        this.burgerMenuOpen = true;
      }
    },
    slideClosed() {
      if (this.burgerMenuOpen === true) {
        this.burgerMenuOpen = false;
      }
    },
    handleSwipe(e) {
      const menuDiv = document.querySelector(".menu-burger__item");
      if (e.target !== menuDiv) {
        this.closeMenu();
      }
    },
    handleBrandClick() {
      const currentRoute = this.$route.path;
      if (this.isLoggedIn) {
        if (currentRoute != "/") {
          this.$router.push("/");
        }
      } else {
        if (currentRoute != "/login") {
          this.$router.push("/login");
        }
      }
    },
    handleWindowResize() {
      this.windowWidth = window.innerWidth;
    }
  }
};
</script>